import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { PostService } from 'src/app/Services/post.service';
import { Post } from 'src/app/Model/post';
import { FileuploadService } from 'src/app/Services/fileupload.service';
import { HttpErrorResponse } from '@angular/common/http';
import{uploa}

@Component({
  selector: 'app-creatpost',
  templateUrl: './creatpost.component.html',
  styleUrls: ['./creatpost.component.css']
})
export class CreatpostComponent implements OnInit {
  postForm!: FormGroup;
  posts: Post[] = [];
  selectedFile: File | null = null;

  constructor(
    private postService: PostService,
    private formBuilder: FormBuilder,
    private fileUploadService: FileuploadService
  ) {
    this.postForm = this.formBuilder.group({
      content: ['', Validators.required],
      img: ['']
    });
  }


  addPost(): void {
    if (this.postForm.valid) {
      const newPost: Post = this.postForm.value as Post;

      // Handle file upload (if a file is selected)
      if (this.selectedFile) {
        this.fileUploadService.uploadFile(this.selectedFile).subscribe(
          (uploadResponse: UploadResonse) => { // Use a more informative response type
            if (uploadResponse instanceof HttpErrorResponse) {
              console.error('Error uploading file:', uploadResponse.error);
              alert('An error occurred while uploading the file.');
            } else {
              console.log('File uploaded successfully:', uploadResponse); // Log entire response (optional)

              // Optionally use the uploaded filename from the response (if provided)
              if (uploadResponse.filename) {
                newPost.filename = uploadResponse.filename;
              }

              this.savePost(newPost); // Save post with (potentially) updated filename
            }
          },
          (error) => {
            console.error('Error uploading file:', error);
            alert('An error occurred while uploading the file.');
          }
        );
      } else {
        // No file selected, save post directly
        this.savePost(newPost);
      }
    } else {
      console.error('Please fill out all required fields.');
      alert('Please fill out all required fields.');
    }
  }


savePost(newPost: Post): void {
    this.postService.addPost(newPost).subscribe(
        () => {
            this.loadPosts();
            this.postForm.reset();
            console.log('Post added successfully!');
        },
        (error) => {
            console.error('Error adding post:', error);
            alert('An error occurred while adding the post.');
        }
    );
}


  onFileSelected(event: any): void {
    this.selectedFile = event.target.files[0];
  }

  loadPosts(): void {
    this.postService.getAllPosts().subscribe(
      (posts) => {
        this.posts = posts;
      },
      (error) => {
        console.error('Error loading posts:', error);
      }
    );
  }

  ngOnInit(): void {
    this.loadPosts();
  }
}
