import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { PostService } from 'src/app/Services/post.service';
import { Post } from 'src/app/Model/post';
import { FileuploadService } from 'src/app/Services/fileupload.service';
import { HttpErrorResponse } from '@angular/common/http';

@Component({
  selector: 'app-creatpost',
  templateUrl: './creatpost.component.html',
  styleUrls: ['./creatpost.component.css']
})
export class CreatpostComponent implements OnInit {
  postForm!: FormGroup;
  posts: Post[] = [];
  selectedFile: File | null = null;
  uploadError: File | null = null;

  constructor(
    private postService: PostService,
    private formBuilder: FormBuilder,
    private fileUploadService: FileuploadService
  ) {
    this.postForm = this.formBuilder.group({
      content: ['', Validators.required],
      img: ['']
    });
  }

  async addPost(): Promise<void> {
    if (this.postForm.invalid) {
      return; // Prevent submission if form is invalid
    }

    const post: Post = this.postForm.value;
    post.pu = new Date(); // Add current date

    try {
      let uploadResponse: any;

      // Upload the image if selected
      if (this.selectedFile) {
        uploadResponse = await this.fileUploadService.uploadFile(this.selectedFile).toPromise();
        // Handle upload response (e.g., success/failure message)
        if (uploadResponse.error) {
          this.uploadError = uploadResponse.error;
          return; // Prevent further processing in case of upload error
        }
        // You might want to update the post object's image property here based on uploadResponse
      }

      // Send the post data along with the image (if uploaded)
      const response = await this.postService.addPost(post).toPromise();
      console.log('Post added successfully:', response); // Handle successful response
      this.postForm.reset(); // Reset the form after successful submission
      this.selectedFile = null;
      this.uploadError = null;
    } catch (error) {
      if (error instanceof HttpErrorResponse) {
        console.error('Error adding post:', error.message);
      } else {
        console.error('Unexpected error:', error);
      }
      this.uploadError = 'An error occurred while adding the post.';
    }
  }

  onFileSelected(event: any): void {
    this.selectedFile = event.target.files[0];
    this.uploadError = null; // Clear previous upload error if any
  }


  loadPosts(): void {
    this.postService.getAllPosts().subscribe(
      (posts) => {
        this.posts = posts;
      },
      (error) => {
        console.error('Error loading posts:', error);
      }
    );
  }

  ngOnInit(): void {
    this.loadPosts();
  }
}
