import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { PostService } from 'src/app/Services/post.service';
import { Post } from 'src/app/Model/post';
import { FileuploadService } from 'src/app/Services/fileupload.service';
import { HttpErrorResponse } from '@angular/common/http';

@Component({
  selector: 'app-creatpost',
  templateUrl: './creatpost.component.html',
  styleUrls: ['./creatpost.component.css']
})
export class CreatpostComponent implements OnInit {
  postForm!: FormGroup;
  posts: Post[] = [];
  selectedFile: File | null = null;

  constructor(
    private postService: PostService,
    private formBuilder: FormBuilder,
    private fileUploadService: FileuploadService
  ) {
    this.postForm = this.formBuilder.group({
      content: ['', Validators.required],
      img: ['']
    });
  }

  addPost(): void {}



  onFileSelected(event: any): void {
    this.selectedFile = event.target.files[0];
  }

  loadPosts(): void {
    this.postService.getAllPosts().subscribe(
      (posts) => {
        this.posts = posts;
      },
      (error) => {
        console.error('Error loading posts:', error);
      }
    );
  }

  ngOnInit(): void {
    this.loadPosts();
  }
}
<form [formGroup]="postForm" (ngSubmit)="addPost()">
  <label for="content">Content:</label>
  <textarea formControlName="content"></textarea>

  <label for="image">Image:</label>
  <input type="file" id="image" name="image" accept="image/*" (change)="onFileSelected($event)">

  <button type="submit">Submit</button>
</form>
