import { Component, OnInit } from '@angular/core';
import { Post } from 'src/app/Model/post';
import { PostService } from 'src/app/Services/post.service';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { CreatcommentComponent } from '../creatcomment/creatcomment.component';

@Component({
  selector: 'app-post',
  templateUrl: './post.component.html',
  styleUrls: ['./post.component.css']
})
export class PostComponent implements OnInit {
  posts: Post[] = [];
  postForm!: FormGroup;
  imageUrl: string | null = null;

  constructor(
    private postService: PostService,
    private formBuilder: FormBuilder,
    private router: Router
  ) {

  }

  ngOnInit(): void {
    this.posts = [];
    this.loadPost();
  }

  loadPost() {
    this.postService.getAllPosts().subscribe(posts => {
      this.posts = posts;
    });
  }
  async displayImage(imageHandle: Post['productimage'][0]): Promise<void> {
    this.imageUrl = null; // Reset image URL before conversion
    await this.convertFileToDataURL(imageHandle); // No explicit return value
  }

  convertFileToDataURL(fileHandle: Post['productimage'][0]): Promise<String> | null {
    if (!fileHandle) {
      return null;
    }
    const reader = new FileReader();
    reader.readAsDataURL(fileHandle.file);
    return new Promise<string>((resolve, reject) => {
      reader.onload = () => resolve(reader.result?.toString() || '');
      reader.onerror = reject;
    }).then(dataURL => dataURL);
  }


  goToCommentForm(postId: number): void {
    this.router.navigate(['/creat-comment', postId]);
  }
}
