import { Component, OnInit } from '@angular/core';
import { Post } from 'src/app/Model/post';
import { PostService } from 'src/app/Services/post.service';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { CreatcommentComponent } from '../creatcomment/creatcomment.component';
import { HttpErrorResponse } from '@angular/common/http';
import { ImageProcessingService } from 'src/app/Services/image-processing.service';
import { map } from 'rxjs';
import { FileHandle } from 'src/app/Model/file-handle';


@Component({
  selector: 'app-post',
  templateUrl: './post.component.html',
  styleUrls: ['./post.component.css']
})
export class PostComponent implements OnInit {
  post: Post | undefined;
  posts: Post[] = [];
  postForm!: FormGroup;
  imageUrl: string | null = null;

  constructor(
    private postService: PostService,
    private formBuilder: FormBuilder,
    private router: Router,
    private imageProcessingService:ImageProcessingService,
    @Inject(MAT_DIALOG_DATA) public data: any
  ) {

  }

  ngOnInit(): void {
    this.posts = [];
    this.loadPost();
  }

  loadPost() {
    this.postService.getAllPosts()
    .pipe(
      map((x: Post[], i) =>x.map((post:Post)=>this.imageProcessingService.createImages(post))))
    .subscribe(
      (resp: Post[])=> {
        console.log(resp);
        this.posts = resp;
      }, (error:HttpErrorResponse)=> {
        console.log(error);
      }
    );
  }
  receiveImages(){

  }
  showImages(post: Post){
    if(post.productimage && post.productimage.length > 0){
      return post.productimage.map((image: FileHandle) => {
        return `<img src="${image.url}" alt="Image ${image.file.name}" />`;
      }).join('');
    } else {
      return 'No images available';
    }
  }



  goToCommentForm(postId: number): void {
    this.router.navigate(['/creat-comment', postId]);
  }
}
