import { Component, OnInit } from '@angular/core';
import { Post } from 'src/app/Model/post';
import { PostService } from 'src/app/Services/post.service';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { CreatcommentComponent } from '../creatcomment/creatcomment.component';

@Component({
  selector: 'app-post',
  templateUrl: './post.component.html',
  styleUrls: ['./post.component.css']
})
export class PostComponent implements OnInit {
  posts: Post[] = [];
  postForm!: FormGroup;

  constructor(
    private postService: PostService,
    private formBuilder: FormBuilder,
    private router: Router
  ) {
    this.postForm = this.formBuilder.group({
      content: ['', Validators.required],
      img: [''] // L'image n'est plus obligatoire
    });
  }

  ngOnInit(): void {
    this.loadPost();
  }

  loadPost() {
    this.postService.getAllPosts().subscribe(posts => {
      this.posts = posts;
    });
  }

  addPost(): void {
    if (this.postForm.valid) {
      const newPost: Post = this.postForm.value as Post;
      this.postService.addPost(newPost).subscribe(() => {
        this.loadPost();
        this.postForm.reset();
        console.log("Post ajouté avec succès !");
      }, error => {
        console.error("Erreur lors de l'ajout du post :", error);
      });
    }
  }

}
<div *ngFor="let post of posts">
    <div class="post">
      <div class="post-header">
        <img src="{{ post.img }}" alt="Image du post">
        <h2>Publié le {{ post.publication_date }}</h2>
      </div>
      <div class="post-content">
        <p>{{ post.content }}</p>
      </div>
      <!--<div class="post-actions">
        <button (click)="likePost()">Likes : {{ post.likes }}</button>
        <button (click)="commentPost()">Commentaires : {{ post.comments.length }}</button>
      </div>-->
      <app-creatcomment [post]="post"></app-creatcomment>
    </div>
  </div>
